{
    "lastKnownUpdatedAt": "2025-11-10T12:37:50.661Z",
    "spec": {
        "openapi": "3.0.1",
        "info": {
            "title": "Project Nova - User & Profile API",
            "description": "A comprehensive API for managing users and their profiles within the Nova ecosystem. This replaces the previous simple spec.",
            "version": "1.0.0"
        },
        "servers": [
            {
                "url": "https://api.nova.app/v1",
                "description": "Production Server"
            }
        ],
        "tags": [
            {
                "name": "Users",
                "description": "Operations about users."
            },
            {
                "name": "Profiles",
                "description": "Operations related to user profiles."
            }
        ],
        "paths": {
            "/users": {
                "get": {
                    "tags": [
                        "Users"
                    ],
                    "summary": "List all users",
                    "description": "Retrieves a paginated list of users.",
                    "operationId": "listUsers",
                    "parameters": [
                        {
                            "name": "limit",
                            "in": "query",
                            "description": "How many items to return at one time (max 100).",
                            "schema": {
                                "type": "integer",
                                "maximum": 100,
                                "default": 20
                            }
                        },
                        {
                            "name": "offset",
                            "in": "query",
                            "description": "The number of items to skip before starting to collect the result set.",
                            "schema": {
                                "type": "integer",
                                "default": 0
                            }
                        }
                    ],
                    "responses": {
                        "200": {
                            "description": "A paged array of users",
                            "content": {
                                "application/json": {
                                    "schema": {
                                        "type": "array",
                                        "items": {
                                            "$ref": "#/components/schemas/User"
                                        }
                                    }
                                }
                            }
                        }
                    }
                },
                "post": {
                    "tags": [
                        "Users"
                    ],
                    "summary": "Create a user",
                    "description": "Creates a new user in the system.",
                    "operationId": "createUser",
                    "requestBody": {
                        "description": "New user object",
                        "required": true,
                        "content": {
                            "application/json": {
                                "schema": {
                                    "$ref": "#/components/schemas/NewUser"
                                }
                            }
                        }
                    },
                    "responses": {
                        "201": {
                            "description": "User created successfully",
                            "content": {
                                "application/json": {
                                    "schema": {
                                        "$ref": "#/components/schemas/User"
                                    }
                                }
                            }
                        },
                        "409": {
                            "$ref": "#/components/responses/Conflict"
                        }
                    }
                }
            },
            "/users/{userId}": {
                "parameters": [
                    {
                        "$ref": "#/components/parameters/UserIdPath"
                    }
                ],
                "get": {
                    "tags": [
                        "Users"
                    ],
                    "summary": "Get user by ID",
                    "operationId": "getUserById",
                    "responses": {
                        "200": {
                            "description": "The user object.",
                            "content": {
                                "application/json": {
                                    "schema": {
                                        "$ref": "#/components/schemas/User"
                                    }
                                }
                            }
                        },
                        "404": {
                            "$ref": "#/components/responses/NotFound"
                        }
                    }
                },
                "delete": {
                    "tags": [
                        "Users"
                    ],
                    "summary": "Delete a user",
                    "operationId": "deleteUser",
                    "responses": {
                        "204": {
                            "description": "User deleted successfully."
                        },
                        "404": {
                            "$ref": "#/components/responses/NotFound"
                        }
                    }
                }
            },
            "/users/{userId}/profile": {
                "parameters": [
                    {
                        "$ref": "#/components/parameters/UserIdPath"
                    }
                ],
                "get": {
                    "tags": [
                        "Profiles"
                    ],
                    "summary": "Get user profile",
                    "operationId": "getUserProfile",
                    "responses": {
                        "200": {
                            "description": "User profile data."
                        }
                    }
                }
            }
        },
        "components": {
            "schemas": {
                "User": {
                    "type": "object",
                    "required": [
                        "id",
                        "username",
                        "email"
                    ],
                    "properties": {
                        "id": {
                            "type": "string",
                            "format": "uuid"
                        },
                        "username": {
                            "type": "string",
                            "example": "john_doe"
                        },
                        "email": {
                            "type": "string",
                            "format": "email"
                        },
                        "createdAt": {
                            "type": "string",
                            "format": "date-time"
                        }
                    }
                },
                "NewUser": {
                    "type": "object",
                    "required": [
                        "username",
                        "email",
                        "password"
                    ],
                    "properties": {
                        "username": {
                            "type": "string",
                            "example": "jane_doe"
                        },
                        "email": {
                            "type": "string",
                            "format": "email"
                        },
                        "password": {
                            "type": "string",
                            "format": "password"
                        }
                    }
                },
                "Error": {
                    "type": "object",
                    "properties": {
                        "code": {
                            "type": "string"
                        },
                        "message": {
                            "type": "string"
                        }
                    }
                }
            },
            "parameters": {
                "UserIdPath": {
                    "name": "userId",
                    "in": "path",
                    "required": true,
                    "description": "The ID of the user.",
                    "schema": {
                        "type": "string",
                        "format": "uuid"
                    }
                }
            },
            "responses": {
                "NotFound": {
                    "description": "The specified resource was not found.",
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/Error"
                            }
                        }
                    }
                },
                "Conflict": {
                    "description": "The resource already exists.",
                    "content": {
                        "application/json": {
                            "schema": {
                                "$ref": "#/components/schemas/Error"
                            }
                        }
                    }
                }
            }
        }
    }
}

