{
    "name": "ErrorResponse",
    "description": "A standard error response object.",
    "schema": {
        "type": "object",
        "properties": {
            "statusCode": {
                "type": "integer",
                "example": 404
            },
            "message": {
                "type": "string",
                "example": "Resource not found."
            },
            "error": {
                "type": "string",
                "example": "Not Found"
            }
        },
        "required": [
            "statusCode",
            "message"
        ]
    }
}

{
    "description": "A standardized error response object, now with a machine-readable meta code.",
    "schema": {
        "type": "object",
        "properties": {
            "statusCode": {
                "type": "integer"
            },
            "message": {
                "type": "string"
            },
            "error": {
                "type": "string"
            },
            "metaCode": {
                "type": "string",
                "example": "RESOURCE_NOT_FOUND"
            }
        },
        "required": [
            "statusCode",
            "message"
        ]
    }
}
